#import "Foundation.h"#import "NSEvent.h"#import "NSResponder.h"#import <Events.h>@class NSMenu;@class NSApplication;@protocol NSApplicationDelegate-(void) applicationDidFinishLaunching;-(void) applicationWillTerminate;@end@interface NSApplication : NSResponder{	BOOL _isRunning;	id<NSApplicationDelegate> _delegate;	NSMenu *_mainMenu;}+(NSApplication*) sharedApplication;-(void) setDelegate: (id<NSApplicationDelegate>)dele;-(id<NSApplicationDelegate>) delegate;-(void) run;-(void) terminate;-(void) setMainMenu: (NSMenu*)menu;-(NSMenu*) mainMenu;// Private:-(BOOL) handleMenuChoice: (long)menuAndItem;@endextern NSApplication *NSApp;