#import "Foundation.h"#include <Quickdraw.h>typedef struct _NSPoint {	float x;	float y;} NSPoint;typedef struct _NSSize {	float width;	float height;} NSSize;typedef struct _NSRect {	NSPoint origin;	NSSize size;} NSRect;NSRect NSMakeRect(float x, float y, float width, float height);NSPoint NSMakePoint(float x, float y);Rect QDRectFromNSRect(NSRect box);NSRect NSRectFromQDRect(Rect box);Point QDPointFromNSPoint(NSPoint pos);NSPoint NSPointFromQDPoint(Point pos);