#import "Foundation.h"#include <Menus.h>@class NSMenu;@class NSMenuItem;@interface NSMenuItem : NSObject{	NSConstantString *_title;	NSConstantString *_keyEquivalent;	NSObject* _target;	SEL _action;	NSMenu *_menu;	NSMenu *_submenu;}-(id) initWithTitle: (NSConstantString*)title target: (NSObject*)target action: (SEL)action keyEquivalent: (NSConstantString*)keyEquiv;-(NSObject*) target;-(SEL) action;-(NSMenu*) menu;-(void) setMenu: (NSMenu*)parent;-(NSMenu*) submenu;-(void) setSubmenu: (NSMenu*)parent;// Private:-(void) install;-(void) debugPrintWithIndent: (int)indent;@end