#import "Foundation.h"#import "NSGeometry.h"@class NSWindow;@class NSView;@interface NSView : NSObject{	NSRect _frame;	NSMutableArray *_subviews;	NSView *_superview;}-(id) initWithFrame: (NSRect)frame;-(void) drawRect: (NSRect)dirtyRect;-(NSRect) frame;-(void) setFrame: (NSRect)box;-(NSRect) bounds;-(NSMutableArray*) subviews;-(void) addSubview: (NSView*)view;-(NSView*) superview;-(void) setSuperview: (NSView*)parent; // Private.-(NSWindow*) window;// Private:-(RGBColor) backgroundColor;-(void) _drawRect: (NSRect)dirtyRect withOffset: (NSPoint)pos;@end@interface NSWindowContentView : NSView{	NSWindow *_window;}-(NSWindow*) window;-(void) setWindow:(NSWindow*)win;@end