#include "AppKit.h"#include <SIOUX.h>@class MyAppDelegate;@interface MyAppDelegate : NSObject{	NSWindow* _mainWindow;}-(void) applicationDidFinishLaunching;-(void) applicationWillTerminate;@end@implementation MyAppDelegate-(void) applicationDidFinishLaunching{	NSView* innerView = nil;	NSButton* buttonView = nil;	NSTextField* textView = nil;	_mainWindow = [[NSWindow alloc] initWithFrame: NSMakeRect(10, 50, 512, 342) title: @"AppKit on Classic!"];	innerView = [[[NSView alloc] initWithFrame: NSMakeRect(100, 100, 300, 200)] autorelease];	[[_mainWindow contentView] addSubview: innerView];	buttonView = [[[NSButton alloc] initWithFrame: NSMakeRect(30, 100, 100, 22)] autorelease];	[buttonView setTitle: @"Toot toot!"];	[[_mainWindow contentView] addSubview: buttonView];	textView = [[[NSTextField alloc] initWithFrame: NSMakeRect(12, 230, 200, 100)] autorelease];	[textView setStringValue: @"The avalanche has already started. It is too late for the pebbles to vote."];	[textView setAutoresizingMask: NSViewMinXMargin | NSViewMaxYMargin | NSViewMaxXMargin | NSViewMinYMargin | NSViewWidthSizable | NSViewHeightSizable];	[[_mainWindow contentView] addSubview: textView];}-(void) applicationWillTerminate{	}@endint main( void ){	NSApplication *app = [NSApplication sharedApplication];	NSObject<NSApplicationDelegate> *dele;	NSMenu * mainMenu = [[NSMenu alloc] initWithTitle: @"MAIN MENU"];	NSMenu * appleMenu = [[NSMenu alloc] initWithTitle: @"ð"];	NSMenu * fileMenu = [[NSMenu alloc] initWithTitle: @"File"];	NSMenuItem * appleMenuParentItem = [[NSMenuItem alloc] initWithTitle: @"ð-PARENT" target: nil action: NULL keyEquivalent: nil];	NSMenuItem * fileMenuParentItem = [[NSMenuItem alloc] initWithTitle: @"FILE-PARENT" target: nil action: NULL keyEquivalent: nil];	NSMenuItem * aboutItem = [[NSMenuItem alloc] initWithTitle: @"About AppKitForClassic..." target: nil action: NULL keyEquivalent: @"B"];	NSMenuItem * quitItem = [[NSMenuItem alloc] initWithTitle: @"Quit" target: app action: @selector(terminate) keyEquivalent: @"Q"];	[appleMenuParentItem setSubmenu: appleMenu];	[fileMenuParentItem setSubmenu: fileMenu];	[mainMenu appendItem: appleMenuParentItem];	[mainMenu appendItem: fileMenuParentItem];		[appleMenu appendItem: aboutItem];	[fileMenu appendItem: quitItem];		[app perform: @selector(setMainMenu:) withObject: mainMenu];	dele = [[MyAppDelegate alloc] init];		[app setDelegate: dele];	[app run];		return 0;}