#include "AppKit.h"#include <SIOUX.h>@class MyAppDelegate;@interface MyAppDelegate : NSObject{	NSWindow* _mainWindow;}-(void) applicationDidFinishLaunching;-(void) applicationWillTerminate;@end@implementation MyAppDelegate-(void) applicationDidFinishLaunching{	_mainWindow = [[NSWindow alloc] initWithFrame: NSMakeRect(10, 50, 200, 100) title: @"AppKit on Classic!"]; }-(void) applicationWillTerminate{	}@endint main( void ){	NSApplication *app = [NSApplication sharedApplication];	NSObject<NSApplicationDelegate> *dele;	NSMenu * mainMenu = [[NSMenu alloc] initWithTitle: @"MAIN MENU"];	NSMenu * appleMenu = [[NSMenu alloc] initWithTitle: @"ð"];	NSMenu * fileMenu = [[NSMenu alloc] initWithTitle: @"File"];	NSMenuItem * appleMenuParentItem = [[NSMenuItem alloc] initWithTitle: @"ð-PARENT" target: nil action: NULL keyEquivalent: nil];	NSMenuItem * fileMenuParentItem = [[NSMenuItem alloc] initWithTitle: @"FILE-PARENT" target: nil action: NULL keyEquivalent: nil];	NSMenuItem * aboutItem = [[NSMenuItem alloc] initWithTitle: @"About AppKitForClassic..." target: nil action: NULL keyEquivalent: @"B"];	NSMenuItem * quitItem = [[NSMenuItem alloc] initWithTitle: @"Quit" target: app action: @selector(terminate) keyEquivalent: @"Q"];	[appleMenuParentItem setSubmenu: appleMenu];	[fileMenuParentItem setSubmenu: fileMenu];	[mainMenu appendItem: appleMenuParentItem];	[mainMenu appendItem: fileMenuParentItem];		[appleMenu appendItem: aboutItem];	[fileMenu appendItem: quitItem];		[app setMainMenu: mainMenu];	dele = [[MyAppDelegate alloc] init];		[app setDelegate: dele];	[app run];		return 0;}